import React, { useEffect, useRef, useState } from 'react';

const Carousel = () => {
  const images = ['hennessy.jpeg', 'best-whisky.webp', 'jd.png', ''];
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  const resetTimeout = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  useEffect(() => {
    resetTimeout();
    if (index === images.length - 1) {
      timeoutRef.current = setTimeout(() => {
        setIndex(0);
      }, 0); // Delay is zero for the last image
    } else {
      timeoutRef.current = setTimeout(() => {
        setIndex((prevIndex) => prevIndex + 1);
      }, 5000);
    }

    return () => {
      resetTimeout();
    };
  }, [index, images.length]);

  const Indicators = () => (
    <div className="absolute bottom-0 left-0 w-full flex justify-center mb-4">
      {images.map((_, i) => (
        <button
          key={i}
          onClick={() => setIndex(i)}
          className={`w-3 h-3 rounded-full mx-2 ${
            i === index ? 'bg-' : 'bg-gray-300'
          }`}
        />
      ))}
    </div>
  );

  return (
    <div className="relative flex overflow-hidden">
      <div className="flex m-auto w-full h-full justify-items-center transition-transform duration-1000 ease-in-out" style={{ transform: `translateX(-${index * 100}%)` }}>
        {images.map((image, i) => (
          <div
            key={i}
            className="flex-none w-full relative bg-cover bg-no-repeat bg-center"
            style={{ backgroundImage: `url(${image})` }}
          >
            <span className=" ">featured products</span>
          </div>
        ))}
      </div>
      <Indicators />
    </div>
  );
};

export default Carousel;
