import React, { useEffect, useRef, useState } from 'react';

const Carousel = () => {
  const images = ['hennessy.jpeg', 'malibu.png', 'malibu.png', 'jd.png'];
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  const resetTimeout = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  useEffect(() => {
    resetTimeout();
    if (index === images.length - 1) {
      timeoutRef.current = setTimeout(() => {
        setIndex(0);
      }, 0); // Delay is zero for the last image
    } else {
      timeoutRef.current = setTimeout(() => {
        setIndex((prevIndex) => prevIndex + 1);
      }, 2000); // Normal delay for other images
    }

    return () => {
      resetTimeout();
    };
  }, [index, images.length]);

  return (
    <div className="flex overflow-hidden">
      <div className="flex transition-transform duration-1000 ease-in-out" style={{ transform: `translateX(-${index * 100}%)` }}>
        {images.map((image, i) => (
          <div key={i} className="flex-none w-full">
            <img
              src={image}
              alt=""
              className="w-full h-full object-cover bg-transparent"
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Carousel;
