import React, { useEffect, useRef, useState } from 'react';

const Carousel = () => {
  const images = ['hennessy.jpeg', 'jd.png', 'hennessy.jpeg', 'jd.png'];
  const normalDelay = 2000;
  const lastImageDelay = 0;
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  const resetTimeout = (delay) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    timeoutRef.current = setTimeout(() => {
      setIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    }, delay);
  };

  useEffect(() => {
    if (index === images.length - 1) {
      resetTimeout(lastImageDelay);
    } else {
      resetTimeout(normalDelay);
    }

    return () => {
      resetTimeout(normalDelay); // Ensures the normal delay is resumed when the component unmounts
    };
  }, [index]);

  return (
    <div>
      {images.map((image, i) => (
        <div key={i} className={i === index ? 'block' : 'hidden'}>
          <img src={image} alt="" />
        </div>
      ))}
    </div>
  );
};

export default Carousel;
